mapper.select:
		select * from product p left outer join product_img pi on p.p_no=pi.p_no
                        join p_category pc on p.p_ctgr_no=pc.p_ctgr_no 
                        where p.p_no=#{p_no}

mapper.update:
	update product p set p.p_title=#{p_title},
                 p.p_desc=#{p_desc},
                 p.p_price=#{p_price},
                 p.p_sell=#{p_sell},
                 p.p_ctgr_no=#{productCategory.p_ctgr_no}
             where p.p_no=#{p_no}
